module.exports = function promiseAll(mapOfPromises) {
	const keys = Object.keys(mapOfPromises)
	const promises = keys.map(key => mapOfPromises[key])
	return Promise.all(promises).then(resultArray => {
		const outputObject = {}

		keys.forEach((key, i) => {
			outputObject[key] = resultArray[i]
		})

		return outputObject
	})
}
